import React from 'react';
import API from "../../utils/API"


const saveData = id => {

    console.log("-----------------------")
    console.log(id)
    console.log("------------------")
    API.saveBook({
      title: this.booktitle

    })
      .then(res => console.log(res.data))
      .catch(err => console.log(err));

  }

function SubmitButton(props){
    return (
        <span className="btn btn-warning submit-btn" {...props} role="button" tabIndex="0" onClick={() => this.saveData(props.bookid)}>
          Submit Button
        </span>
      );

}

export default SubmitButton;
